//增加角色
StringJsonVO::Wrapper RoleController::executeAddRole(const RoleAddDTO::Wrapper& dto, const PayloadDTO& payload)
{
	auto jvo = StringJsonVO::createShared();

	//参数校验
	if (!dto->name || !dto->data || !dto->root || !dto->auth) {
		jvo->init(nullptr, RS_PARAMS_INVALID);
		return jvo;
	}

	if (dto->name->empty() || dto->data->empty() || dto->root->empty() || dto->auth->empty()) {

		jvo->setStatus(ResultStatus(u8"传入数据为空", 10001));
		return jvo;
	}

	RoleService  service;
	// 检查角色名称是否已存在
	if (service.isRoleNameExists(dto->name)) {
		jvo->setStatus(ResultStatus(u8"新增角色已存在", 10002));
		return jvo;
	}
	// 执行数据新增
	dto->setPayload(&payload);
	std::string id = service.saveData(dto);
	if (id != "") {
		jvo->success("");
	}
	else
	{
		jvo->fail("");
	}
	//响应结果
	return jvo;

}

//删除角色
ListJsonVO<DeleteResultDTO::Wrapper>::Wrapper RoleController::execRemoveRole(const List<String>& ids)
{
	// 定义返回数据对象
	auto jvo = ListJsonVO<DeleteResultDTO::Wrapper>::createShared();

	// 遍历每个ID并进行合法性验证
	for (const auto& id : *ids) {
		// 1. 检查ID是否为空
		if (id == nullptr || id->empty()) {
			jvo->setStatus(ResultStatus(u8"传入数据为空", 10001));
			return jvo;
		}
		//2. 检查ID是否是UUID格式
		if (!uuid::is_valid_uuid(*id)) {
			jvo->setStatus(ResultStatus(u8"传入ID格式不正确", 10003));
			return jvo;
		}
	}

	RoleService service;
	// 在删除前检查每个角色与用户的关联数量
	SqlSession sqlSession;
	for (const auto& id : *ids) {
		// 获取角色详情以获取角色名称
		auto roleDetail = service.getById(id);
		if (!roleDetail || !roleDetail->name) {
			continue; // 跳过无法获取名称的角色
		}
		try {
			uint64_t userCount = service.checkRoleUserRelation(id);
			if (userCount > 0) {
				// 清空data列表
				jvo->data = oatpp::List<oatpp::Object<DeleteResultDTO>>::createShared();

				// 创建一个只设置userCount字段的DeleteResultDTO
				auto resultDTO = DeleteResultDTO::createShared();
				resultDTO->userCount = userCount; // 只设置userCount字段

				// 将DTO添加到列表中
				jvo->addData(resultDTO);

				// 设置错误状态
				jvo->setStatus(ResultStatus(u8"存在数据关联,删除失败!", 10002));
				return jvo;
			}
		}
		catch (const std::exception& e) {
			// 忽略查询异常，继续处理其他角色
		}
	}
	// 为每个删除的ID创建删除结果DTO
	for (const auto& id : *ids) {
		auto resultDTO = DeleteResultDTO::createShared();
		// 设置删除的ID
		resultDTO->deletedId = id;

		// 在删除前调用getById方法获取角色名称
		auto roleDetail = service.getById(id);
		if (roleDetail && roleDetail->name) {
			resultDTO->deletedName = roleDetail->name;
		}
		else {
			// 如果无法获取角色名称，仍使用ID作为名称
			resultDTO->deletedName = id;
		}
		// 添加到响应列表中
		jvo->addData(resultDTO);
	}


	// 执行数据删除
	if (service.removeData(ids)) {
		// 设置成功状态
		jvo->setStatus(RS_SUCCESS);
	}
	else
	{
		jvo->setStatus(ResultStatus(u8"输入的正确ID删除成功,错误ID删除失败!", 10003));
	}
	// 响应结果
	return jvo;
}

//修改角色
StringJsonVO::Wrapper RoleController::execModifyRole(const RoleListDTO::Wrapper& dto, const PayloadDTO& payload)
{
	// 定义返回数据对象
	auto jvo = StringJsonVO::createShared();
	// 参数校验
	if (!dto->id)
	{
		jvo->init(nullptr, RS_PARAMS_INVALID);
		return jvo;
	}
	RoleService service;
	// 检查角色是否已存在
	if (!service.isRoleIDExists(dto->id)) {
		jvo->init(nullptr, RS_PARAMS_INVALID);
		return jvo;
	}

	// 执行数据修改
	dto->setPayload(&payload);

	if (service.updateData(dto)) {
		jvo->success(dto->id);
	}
	else
	{
		jvo->fail(dto->id);
	}
	// 响应结果
	return jvo;
}
// 角色分页查询接口实现
RoleListPageJsonVO::Wrapper RoleController::executeQueryALL(const RoleQuery::Wrapper& query)
{
	auto jsonVo = RoleListPageJsonVO::createShared();

	try {
		auto roleService = std::make_shared<RoleService>();
		auto result = roleService->listByCondition(query);

		if (result) {
			jsonVo->success(result);
		}
		else {
			jsonVo->setStatus(RS_SERVER_ERROR);
		}
	}
	catch (const std::exception& /*e*/) {
		jsonVo->setStatus(RS_SERVER_ERROR);
	}

	return jsonVo;
}

// 角色详情查询接口实现
RoleDetailJsonVO::Wrapper RoleController::executeQueryDetail(const String& id)
{
	auto jsonVo = RoleDetailJsonVO::createShared();

	if (!id || id->empty()) {
		jsonVo->setStatus(RS_PARAMS_INVALID);
		return jsonVo;
	}

	try {
		auto roleService = std::make_shared<RoleService>();
		auto result = roleService->getById(id);

		if (result && result->id && !result->id->empty()) {
			jsonVo->success(result);
		}
		else {
			jsonVo->setStatus(ResultStatus("not found", 404));
		}
	}
	catch (const std::exception& /*e*/) {
		jsonVo->setStatus(RS_SERVER_ERROR);
	}

	return jsonVo;
}

// 获取角色列表
RoleNameListJsonVO::Wrapper RoleController::execGetRoleList(const RoleListQuery::Wrapper& query)
{
	auto jvo = RoleNameListJsonVO::createShared();
	
	// 参数验证
	if (!query) {
		jvo->init(nullptr, RS_PARAMS_INVALID);
		return jvo;
	}
	
	RoleService service;
	auto result = service.getRoleListByKeyword(query->keyword);
	
	if (result) {
		jvo->success(result);
	}
	else {
		jvo->fail(nullptr);
	}
	
	return jvo;
}

